import * as actionTypes from '../actions/actionTypes'
import {updateObject} from '../utitlity'

const initialState = {
    token: null,
    userId: null,
    error: null,
    loading: false,
    authRedirect: '/'
}

const setAuthRedirectPath = (state, action) => {
    return updateObject(state, {authRedirect: action.path})
}

const authStart = (state, action) => {
    return updateObject(state, {error: null, loading: true})
}

const authSuccess = (state, action) => {
    return updateObject(state, {token: action.token, userId: action.userId, error: null, loading: false})
}

const authFail = (state, action) => {
    return updateObject(state, {error: action.errror, loading: false})
}

const authLogout = (state, action) => {
    return updateObject(state, {token: null, userId: null})
}

const reducer = (state = initialState, action) => {
    switch(action.type){
        case actionTypes.AUTH_START:
            return authStart(state, action);
        case actionTypes.AUTH_SUCCESS:
            return authSuccess(state, action);
        case actionTypes.AUTH_FAIL:
            return authFail(state, action);
        case actionTypes.AUTH_LOGOUT: 
            return authLogout(state, action);
        default:
            return state;
    }
}

export default reducer;
